import { useContext, useEffect, useRef } from 'react'
import { Link } from 'react-router-dom'
import { AppContext } from '../../context/context'

import icon_human from '../../assets/img/icon-human.png'
import icon_alien from '../../assets/img/icon-alien.png'



const Card = props => {

	const { character } = props
	const { deleteCharacter } = useContext(AppContext)
	const img = useRef()
	const body = useRef()
	const card_content = useRef()

	const deleteEnter = () => {
		card_content.current.classList.add('opacity-50')
	}

	const deleteLeave = () => {
		card_content.current.classList.remove('opacity-50')
	}

	const remove = () => deleteCharacter( character.id )


	return (
		<div 
			className="w-[270px] transition-all duration-500 relative md:w-auto" 
			id={ `character-${character.id}` }>

			<button 
				onClick={ remove }
				className="bg-indigo-900 text-indigo-300 text-2xl leading-none 
									w-7 h-7 right-2 top-2 absolute 
									rounded-full z-10"
				onMouseEnter={ deleteEnter }
				onMouseLeave={ deleteLeave }>
				&times;
			</button>

			<div ref={ card_content }>
				<img 
					src={ character.image } 
					alt={ character.name }
					className="w-full h-48 object-cover object-top transition-opacity duration-500 rounded-t-xl" />

				<div 
					className="bg-indigo-900 text-indigo-300 px-6 py-5 relative rounded-b-xl">

					<div className="bg-indigo-900 p-2 left-3.5 -top-4 absolute rounded-full">
						{ 'Human' === character.species ? 
							( <img 
									src={ icon_human } 
									alt="humano" 
									className="w-6" />
								) : 
							( <img 
									src={ icon_alien } 
									alt="alien" 
									className="w-6" /> )
						}
					</div>

					<div className="text-indigo-200 font-semibold whitespace-nowrap text-ellipsis overflow-hidden">{ character.name }</div>
					<div className="text text-xs">{ character.species }</div>

				</div>
			</div>
		</div>
  );
}


/**
 * 
<div className={styles.div}>
			<Link to={`/Detail/${personaje.id}`}>
				<div className={styles.divImagen}>
					<img className={styles.imagen} src={personaje.image} alt="" />
				</div>
			</Link>
			<div className={styles.divNombre}>
				<h2 className={styles.nombre}>{personaje.name}</h2>
			</div>
			<div className={styles.divInfo}>
				<h2 className={styles.species}>{personaje.species}</h2>
				<button
					className={styles.boton}
					onClick={onClose}
				>
					X
				</button>
				<h2 className={styles.genero}>{personaje.gender}</h2>
			</div>
		</div>
 */

export default Card
