	}



  return (
    <>
			<AppContext.Provider 
				value={{
					characters,
					addCharacter,
					updateCharacters,
					deleteCharacter,
					addRandomCharacter,
					loading,
					handleLoading,
					clearAll
				}}>
				<main id="mainWrapper">
					<NavBar path="/" />
					<Outlet/>
					<Footer />
				</main>
			</AppContext.Provider>
    </>
  );
}

export default App;
