import { useContext } from 'react'
import { 
	Link, 
	useNavigate 
} from 'react-router-dom'
import { AppContext } from '../../context/context'
import localforage from 'localforage'
import { 
	HiOutlineTrash,
	HiOutlinePlusCircle,
	HiOutlineHeart 
} from 'react-icons/hi'

import SearchBar from '../SearchBar/SearchBar'

import css from './NavBar.module.css'
import ImgLogo from '../../assets/img/logo.png'


export default NavBar => {

	const navigate = useNavigate()

	const { 
		addRandomCharacter,
		loading,
		clearAll,
		auth,
		updateAuth,
		characters
	} = useContext(AppContext)


	const totalFav = () => {
		const favs = characters.filter()
	}


	const logout = () => {

		localforage.removeItem('user')
			.then(() => {
				updateAuth(null)
				navigate('/login')
			})
	}


  return (
		<nav className="py-2 relative z-30">
			<div className="w-full max-w-6xl px-6 mx-auto xl:px-0">
				<div className="grid gap-6 md:flex md:justify-between md:items-center">

					<div className="flex justify-center">
						<Link to="/">
							<img 
								src={ ImgLogo } 
								alt="Rick & Morty" 
								className="brand w-32 md:w-48" />
						</Link>
					</div>


				{auth && (
					<div className="grid sm:flex sm:justify-between sm:gap-x-5 md:justify-end">
						<SearchBar />

						<div className="flex justify-center gap-4 mt-4 sm:m-0">
							<button 
								className={`${css.button} ${loading ? 'active': ''}`} 
								onClick={ addRandomCharacter }
								disabled={ loading }>
									<div className="md:w-28 md:h-5 md:flex items-center gap-2">
										<HiOutlinePlusCircle className={ loading ? 'invisible' : 'text-2xl leading-none' } />
										<span className={ loading ? 'hidden' : 'hidden md:inline-block'}>Personaje</span>
										<span className={ loading ? '' : 'hidden' }>Cargando...</span>
									</div>
							</button>

							<button 
								className={ css.button }
								onClick={ clearAll }>
								<HiOutlineTrash className="text-2xl leading-none" />
							</button>

							<div className="flex items-center gap-x-2">
								<HiOutlineHeart className="text-red-700 text-4xl leading-none"/>

								<button 
									className="text-white text-opacity-60 transition-all 
															hover:text-opacity-100"
									onClick={ logout }>
									Salir
								</button>
							</div>

						</div>
					</div>
				)}

				</div>
			</div>



			{/* <div className={styles.controles}>
				<button className={styles.addAll}>AÃ±adir Todas</button>
			</div> */}
		</nav>
  );
}
