import React, { 
	useState, 
	useEffect,
	useContext 
} from 'react'
import { AppContext } from '../../context/context'

import css from './Cards.module.css'

import loader from '../../assets/img/loader.png'
import empty from '../../assets/img/empty.png'
import Card from '../Card/Card'


export default Cards => {

	const { 
		characters, 
		updateCharacters,
		loading
	} = useContext(AppContext)


	useEffect(() => {
		const arr = []
		for(let i = 1; i <= 10; i++) arr.push(i)

		// fetch(`https://rickandmortyapi.com/api/character/${arr.toString()}`)
		// 	.then(resp => resp.json())
		// 	.then(data => {
		// 		updateCharacters(Object.values(data))
		// 	})
	}, [])


  return (
		<section className="mt-10">
			<div className="w-full max-w-6xl px-6 mx-auto xl:px-0">
				{ (characters.length || loading) ? (
					<div 
						className="grid gap-x-4 gap-y-10 justify-center 
												sm:grid-cols-2 
												md:grid-cols-3 
												lg:grid-cols-4 lg:gap-x-6">

						{ loading ? (
								<div className="">
									<div 
										className="bg-indigo-800 bg-opacity-30 border 
																border-indigo-800 w-[270px] h-full min-h-[250px] 
																grid place-content-center rounded-xl md:w-auto">
										<img src={ loader } alt="loader" className={ css.loader } />
									</div>
								</div>
							) : ''}

						{ 
							characters.map((c, i) => (
								<div key={ c.id } 
									className={ i % 2 ? 'block' : 'flex justify-end md:block' }>
									<Card character={ c } />
								</div>
							))
						}
					</div>
				) : (
					<div className="pt-24 grid text-center">
						<img 
							src={ empty } 
							alt="No hay caracteres" 
							className="w-52 mx-auto" />
						<div className={ css['error-title'] }>Vaya!</div>
						<div className={ css['error-info'] }>Parece que no hay personajes por aqu√≠...</div>
						<div className={ css['error-message'] }>
							{ `Puedes buscar uno o hacer click en `}
							<span className="bg-white bg-opacity-20 px-2 pt-0.5 pb-1 font-semibold rounded-full">+ Personaje</span> 
							{` para agregar un nuevo personaje.` }
						</div>
					</div>
				)}
			</div>
		</section>
  );
}
