import { useState } from 'react'
import { validate } from './validate'

import css from './Form.module.css'


const form_model = { user: '', password: '' }


const Form = (props) => {
	const { login } = props

	const [form, setForm] = useState({ user: 'rick@rickandmortyapp.com', password: 'rick&Morty' })
	const [errors, setErrors] = useState({...form_model})
	const [display_errors, setDisplayErrors] = useState(false)


	const handleChange = e => {
		setForm({...form, [e.target.name]: e.target.value })
		setErrors(validate({...form, [e.target.name]: e.target.value }))
	}


	const clearErrors = () => setDisplayErrors(false)


	const handleSubmit = e => {
		e.preventDefault()

		console.log('form', form)
		console.log('display_errors', display_errors)
		console.log('errors', errors)

		if( Object.values(errors).length === 0 ){
			login(form);
			setForm({
				user: "",
				password: "",
			});
			setErrors({
				user: "",
				password: "",
			});
		} else {
			// alert("Debes corregir los errores");
			setDisplayErrors(true)
		}
	}


	return (
		<>
			<div className="w-full max-w-xs px-6 pt-32 mx-auto xl:px-0">
				<form 
					onSubmit={ handleSubmit } 
					className="w-full grid gap-y-4">

					<div className={ css.fieldset }>
						<input
							type="text"
							className={ css.field }
							placeholder="Usuario"
							name="user"
							value={ form.user }
							onChange={ handleChange }
							onKeyUp={ clearErrors }
						/>
						{ display_errors && ( <p className={ css.errors }>{errors.user}</p> ) }

					</div>

					<div className={ css.fieldset }>
						<input
							type="password"
							value={ form.password }
							className={ css.field }
							placeholder="Contraseña"
							name="password"
							onChange={ handleChange }
							onKeyUp={ clearErrors }
						/>
						{ display_errors && ( <p className={ css.errors }>{errors.password}</p> ) }
					</div>

					<button className={ css.btn }>
						Entrar
					</button>
				</form>

				<div className="text-white pt-10 grid">
					<div>Usuario: rick@rickandmortyapp.com</div>
					<div>Contraseña: rick&Morty</div>
				</div>
			</div>
		</>
	);
}

export default Form
