import React, { useState } from 'react'
import { HiOutlinePlusCircle } from 'react-icons/hi'

import css from './SearchBar.module.css'


export default function SearchBar(props) {

	const [input, setInput] = useState("")
	const [ characters, setCharacters ] = useState(null)

	const searchCharacter = async e => {
		if( e.target.value < 3 ){
			setCharacters(null)
			return
		}

		await fetch(`https://rickandmortyapi.com/api/character/?name=${e.target.value}`)
			.then(resp => resp.json())
			.then(data => {
				if( data.erro ){
					setCharacters(null)
				}else{
					setCharacters(data.results)
				}
			})

		console.log('characters', characters)
	}

	const addCharacter = e => {
		e.preventDefault()
		console.log('Add character')
	}


  return (
		<div className={ css['search-wrapper']}>
			<div className={ css.search }>

				<input
					placeholder="Escribe el nombre de un personaje"
					className={ css['search-input'] }
					type="text"
					value={ input }
					onKeyUp={ searchCharacter }
					onChange={(evento) => setInput(evento.target.value)}
				/>

				{/* <button className={ css['search-btn'] }>
					<HiOutlinePlusCircle className="text-2xl leading-none" />
				</button> */}
			</div>

			{
				characters && (
					<div className={ css['search-results'] }>
						{
							characters.map((c, i) => (
								i < 11 && (
									<div 
										key={c.id} 
										className={ css.item }
										onClick={ addCharacter }>
											<img 
												src={ c.image } 
												alt={ c.name }
												className="w-8 h-8 object-cover rounded-full" />
											{ c.name }
									</div>
								)
							))
						}
					</div>
				)
			}
		</div>
  )
}
