import React, { useState, useEffect } from 'react'

import loader from '../../assets/img/loader.png'
import Card from '../Card/Card'


const Cards = (props) => {
  // const { characters, eliminar } = props
	const [ characters, setCharacters ] = useState([])

	useEffect(() => {
		const arr = []
		for(let i = 1; i <= 10; i++) arr.push(i)

		fetch(`https://rickandmortyapi.com/api/character/${arr.toString()}`)
			.then(resp => resp.json())
			.then(data => {
				console.log('data', data)
				setCharacters(Object.values(data))

				// console.log('characters', characters)


				// if (data.name) {
				// 	setCharacters((oldChars) => [...oldChars, data]);
				// } else {
				// 	window.alert("No hay personajes con ese ID");
				// }
			})

	}, [])


  return (
		<section className="mt-10">

			<div className="">
				<img src={ loader } alt="loader" class="loader" />
			</div>

			<div className="w-full max-w-6xl px-6 mx-auto xl:px-0">
				<div 
					className="grid gap-x-4 gap-y-10 justify-center 
											sm:grid-cols-2 
											md:grid-cols-3 
											lg:grid-cols-4 lg:gap-x-6">
					{ 
						characters && characters.map((c, i) => (
							<div key={ c.id } className={ i % 2 ? 'block' : 'flex justify-end md:block' }>
								<Card character={ c } />
							</div>
						))
					}
				</div>
			</div>
		</section>
		// <div className={styles.contenedor}>
		// 	{ characters && characters.map((personaje) => (
		// 		<div key={personaje.id} className={styles.card}>
		// 			{<Card personaje={personaje} eliminar={eliminar} />}
		// 		</div>
		// 	)) }
		// </div>
  );
}

export default Cards
