import { useState } from 'react'
import { validate } from './validate'

import css from './Login.module.css'



const Form = (props) => {
  const { login } = props;
  const [UserData, setUserData] = useState({
    user: "",
    password: "",
  });
  const [errors, setErrors] = useState({
    user: "",
    password: "",
  });

  const handleChange = (event) => {
    const key = event.target.name;
    const value = event.target.value;
    setUserData({ ...UserData, [key]: value });
    setErrors(validate({ ...UserData, [key]: value }));
  };

  const handleSubmit = function (evento) {
    evento.preventDefault();
    if (Object.values(errors).length === 0) {
      login(UserData);
      setUserData({
        user: "",
        password: "",
      });
      setErrors({
        user: "",
        password: "",
      });
    } else {
      alert("Debes corregir los errores");
    }
  };

  return (
    <>
      <div className={css.container}>
        <form onSubmit={ handleSubmit } className={css.form}>
          <div className={css.divusername}>
            <input
              className={css.inputs}
              placeholder="Username"
              name="user"
              type="text"
              value={UserData.user}
              onChange={handleChange}
            />
            <p className={css.danger}>{errors.user}</p>
          </div>
          <div className={css.divpassword}>
            <input
              className={css.inputs}
              placeholder="Password"
              name="password"
              type="text"
              value={UserData.password}
              onChange={handleChange}
            />
            <p className={css.danger}>{errors.password}</p>
          </div>
          <button type="submit" className={css.login}>
            Log in
          </button>
        </form>
      </div>
    </>
  );
}

export default Form
