import { useState, useEffect, useContext } from 'react'
import { useParams, useNavigate } from 'react-router-dom'

import { AppContext } from '../../context/context'

import style from "./Detail.module.css";

export default Detail => {

	const navigate = useNavigate()
	const { characters } = useContext(AppContext)
  const { id } = useParams()
	const character = characters.filter(e => e.id === Number(id))[0]

	useEffect(() => {
		if( !characters.length ) navigate('/')
	}, [])

  // const [character, setCharacter] = useState({});

  // useEffect(() => {
  //   fetch(`https://rickandmortyapi.com/api/character/${id}`)
  //     .then((response) => response.json())
  //     .then((char) => {
  //       if (char.name) {
  //         setCharacter(char);
  //       } else {
  //         window.alert("No hay personajes con ese ID");
  //       }
  //     })
  //     .catch((err) => {
  //       window.alert("No hay personajes con ese ID");
  //     });
  //   return setCharacter({});
  // }, [id]);

  // const origin = { ...character.origin }

	console.log( character )

  return (
		<section className="mt-10">
			<div className="w-full max-w-xl px-6 mx-auto xl:px-0">
				{ character && (
					<div className="grid gap-y-8">
						<div>
							<img 
								src={ character.image } 
								alt={ character.name }
								className="w-full h-72 object-cover object-top rounded-xl" />
						</div>

						<div class="character-description">
							<div className="character-description-item">
								<span>Nombre</span>
								<span>{ character.name }</span>
							</div>

							<div className="character-description-item">
								<span>Especie</span>
								<span>{ character.species === 'Human' ? 'humano' : 'alien' }</span>
							</div>

							<div className="character-description-item">
								<span>Género</span>
								<span>{ character.gender === 'Male' ? 'masculino' : 'femenino' }</span>
							</div>

							<div className="character-description-item">
								<span>Género</span>
								<span>{ character.gender === 'Male' ? 'masculino' : 'femenino' }</span>
							</div>
						</div>

					</div>
				)}


			</div>
		</section>
  );
}
