import React, { useState } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { AppContext } from './context/context'

import './App.css'

import NavBar from './components/NavBar/NavBar'
import Footer from './components/Footer/Footer'


function App() {

	const [ characters, setCharacters ] = useState([])
	const [ favorites, setFavorites ] = useState(false)
	const [ filters, setFilters ] = useState({
		gender: null,
		order: 'asc'
	})
	const [ loading, setLoading ] = useState(false)
	const [ auth, setAuth ] = useState(false)
  const navigate = useNavigate()


	// Get character by ID
	const getCharacter = id => {
		return fetch(`https://rickandmortyapi.com/api/character/${id}`)
			.then(resp => resp.json())
			.then(data => data)
	}



	// Delete character
	const deleteCharacter = id => {
		const arr = characters.filter(e => e.id !== id)
		document.getElementById(`character-${id}`).classList.add('-translate-y-[30px]', 'opacity-0', 'pointer-events-none')
		setTimeout(() => setCharacters(arr), 510)
	}

	// Add random character
	const addRandomCharacter = async () => {
		navigate('/')
		setLoading(true)
		const id = Math.floor(Math.random() * (Math.floor(1) - Math.ceil(826) + 1) + Math.ceil(826))
		const chars = [...characters]

		// Check for existent character
		if( chars.find( e => e.id === id ) ){
			addRandomCharacter()
			return
		}

		const char = await getCharacter(id)
		chars.unshift(char)
		setLoading(false)
		setCharacters(chars)
	}


	// Set loading
	const handleLoading = status => setLoading(status)


	// Clear all characters
	const clearAll = () => {
		navigate('/')
		setCharacters([])
	}


	// Update all characters
	const updateCharacters = num => {
		clearAll()
		return fetch(`https://rickandmortyapi.com/api/character/${num ?? ''}`)
			.then(resp => resp.json())
			.then(data => {
				setCharacters(data.results)
			})
	}


	// Add character
	const addCharacter = el => {
		const chars = [...characters]

		// Check for existent character
		if( chars.find( e => e.id === el.id ) ) return

		chars.unshift(el)
		setCharacters(chars)
	}


	// Authorization
	const updateAuth = user => setAuth(user)


	const addFav = id => {
		const chars = [...characters]
		const idx = chars.findIndex(e => e.id === id)
		chars[idx].favorite = true
		setCharacters(chars)

		const favs = [...favorites]
		favs.push(chars[idx])
		setFavorites(favs)
	}


	const removeFav = id => {
		const chars = [...characters]
		const idx = chars.findIndex(e => e.id === id)
		delete chars[idx].favorite
		setCharacters(chars)

		const favs = [...favorites]
		const iidx = favs.findIndex(e => e.id === id)
		favs.splice(iidx, 1)
		setFavorites(favs)
	}



  return (
    <>
			<AppContext.Provider 
				value={{
					characters,
					addCharacter,
					updateCharacters,
					deleteCharacter,
					addRandomCharacter,
					loading,
					handleLoading,
					clearAll,
					auth,
					updateAuth,
					addFav,
					removeFav,
					favorites,
					filters
				}}>
				<main id="mainWrapper">
					<NavBar />
					<Outlet/>
					<Footer />
				</main>
			</AppContext.Provider>
    </>
  );
}

export default App;
