import { useState } from 'react'
import { validate } from './validate'

import css from './Form.module.css'



const Form = (props) => {
  const { login } = props;
  const [UserData, setUserData] = useState({
    user: "",
    password: "",
  });
  const [errors, setErrors] = useState({
    user: "",
    password: "",
  });

  const handleChange = (event) => {
    const key = event.target.name;
    const value = event.target.value;
    setUserData({ ...UserData, [key]: value });
    setErrors(validate({ ...UserData, [key]: value }));
  };

  const handleSubmit = function (evento) {
    evento.preventDefault();
    if (Object.values(errors).length === 0) {
      login(UserData);
      setUserData({
        user: "",
        password: "",
      });
      setErrors({
        user: "",
        password: "",
      });
    } else {
      alert("Debes corregir los errores");
    }
  };


	return (
		<>
			<div className="max-w-xl px-6 mx-auto xl:px-0">
				<form 
					onSubmit={ handleSubmit } 
					className="grid gap-y-4">

					<div className={ css.fieldset }>
						<input
							className={ css.field }
							placeholder="Username"
							name="user"
							type="text"
							value={UserData.user}
							onChange={handleChange}
						/>
						<p className={css.danger}>{errors.user}</p>
					</div>

					<div className={ css.fieldset }>
						<input
							className={ css.field }
							placeholder="Password"
							name="password"
							type="text"
							value={UserData.password}
							onChange={handleChange}
						/>
						<p className={css.danger}>{errors.password}</p>
					</div>
					<button type="submit" className={css.login}>
						Log in
					</button>
				</form>
			</div>
		</>
	);
}

export default Form
