import { useContext, useRef } from 'react'
import { Link } from 'react-router-dom'
import { AppContext } from '../../context/context'

import icon_human from '../../assets/img/icon-human.png'
import icon_alien from '../../assets/img/icon-alien.png'



const Card = props => {

	const { character } = props
	const { deleteCharacter } = useContext(AppContext)

	const remove = () => deleteCharacter( character.id )

	const btn_delete = useRef()
	const img = useRef()
	const body = useRef()

	const highlightContent = status => {
		if( !character ) return

		console.log( img.current )

		switch(status){
			case 'in':
				// img.current.classList.add('opacity-50')
				// body.current.classList.add('opacity-50')
			case 'out':
				// img.current.classList.remove('opacity-50')
				// body.current.classList.remove('opacity-50')
		}
	}

	return (
		<div 
			className="w-[270px] transition-all duration-500 relative md:w-auto" 
			id={ `character-${character.id}` }>

			<button 
				onClick={ remove }
				className="bg-indigo-900 text-indigo-300 text-2xl leading-none 
									w-7 h-7 right-2 top-2 absolute transition-all 
									rounded-full z-10"
				ref={ btn_delete }>&times;</button>

			<img 
				src={ character.image } 
				alt={ character.name }
				ref={ img }
				className="w-full h-48 object-cover object-top rounded-t-xl" />

			<div 
				className="bg-indigo-900 text-indigo-300 px-6 py-5 relative rounded-b-xl"
				ref={ body }>

				<div className="bg-indigo-900 p-2 left-3.5 -top-4 absolute rounded-full">
					{ 'Human' === character.species ? 
						( <img 
								src={ icon_human } 
								alt="humano" 
								className="w-6" />
							) : 
						( <img 
								src={ icon_alien } 
								alt="alien" 
								className="w-6" /> )
					}
				</div>

				<div className="text-indigo-200 font-semibold whitespace-nowrap text-ellipsis overflow-hidden">{ character.name }</div>
				<div className="text text-xs">{ character.species }</div>

			</div>
		</div>
  );
}


