import { useState } from 'react'
import { validate } from './validate'

import css from './Form.module.css'


const form_model = { user: '', password: '' }


const Form = (props) => {
	const { login } = props

	const [form, setForm] = useState({...form_model})
	const [errors, setErrors] = useState({...form_model})


	const handleChange = (event) => {
		setForm({...form, [event.target.name]: event.target.value })
		setErrors(validate({...form, [event.target.name]: event.target.value }))

		console.log('errors', errors)
	}


	const handleSubmit = e => {
		e.preventDefault()

		if( Object.values(errors).length === 0 ){
			login(form);
			setForm({
				user: "",
				password: "",
			});
			setErrors({
				user: "",
				password: "",
			});
		} else {
			alert("Debes corregir los errores");
		}
	};


	return (
		<>
			<div className="w-full max-w-sm px-6 pt-32 mx-auto xl:px-0">
				<form 
					onSubmit={ handleSubmit } 
					className="w-full grid gap-y-4">

					<div className={ css.fieldset }>
						<input
							type="text"
							className={ css.field }
							placeholder="Username"
							name="user"
							value={ form.user }
							onChange={handleChange}
						/>
						<p className={css.danger}>{errors.user}</p>
					</div>

					<div className={ css.fieldset }>
						<input
							type="text"
							value={ form.password }
							className={ css.field }
							placeholder="Password"
							name="password"
							onChange={handleChange}
						/>
						<p className={css.danger}>{errors.password}</p>
					</div>

					<button className={ css.btn }>
						Entrar
					</button>
				</form>
			</div>
		</>
	);
}

export default Form
