import React, { useState, useEffect } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { AppContext } from './context/context'
import { 
	AuthProvider, 
	useAuth 
} from './context/auth'

import './App.css'

import NavBar from './components/NavBar/NavBar'
import Footer from './components/Footer/Footer'


function App() {

	const [ characters, setCharacters ] = useState([])
	const [ loading, setLoading ] = useState(false)
  const [ access, setAccess ] = useState(false)
  const navigate = useNavigate()
  const username = process.env.USER
  const password = process.env.PASSWORD

	// Get character by ID
	const getCharacter = id => {
		return fetch(`https://rickandmortyapi.com/api/character/${id}`)
			.then(resp => resp.json())
			.then(data => data)
	}

	// Update all characters
	const updateCharacters = data => setCharacters(data)


	// Delete character
	const deleteCharacter = id => {
		const arr = characters.filter(e => e.id !== id)
		document.getElementById(`character-${id}`).classList.add('-translate-y-[30px]', 'opacity-0', 'pointer-events-none')
		setTimeout(() => setCharacters(arr), 510)
	}

	const addRandomCharacter = async () => {
		navigate('/')
		setLoading(true)
		const id = Math.floor(Math.random() * (Math.floor(1) - Math.ceil(826) + 1) + Math.ceil(826))
		const chars = [...characters]

		// Check for existent character
		if( chars.find( e => e.id === id ) ){
			addRandomCharacter()
			return
		}

		const char = await getCharacter(id)
		chars.unshift(char)
		setLoading(false)
		setCharacters(chars)
	}

	const handleLoading = status => setLoading(status)

	const clearAll = () => {
		navigate('/')
		setCharacters([])
	}

	const addCharacter = el => {
		const chars = [...characters]

		// Check for existent character
		if( chars.find( e => e.id === el.id ) ) return

		chars.unshift(el)
		setCharacters(chars)
	}





  return (
    <>
			<AuthProvider>
				<AppContext.Provider 
					value={{
						characters,
						addCharacter,
						updateCharacters,
						deleteCharacter,
						addRandomCharacter,
						loading,
						handleLoading,
						clearAll
					}}>
					<main id="mainWrapper">
						<NavBar path="/" />
						<Outlet/>
						<Footer />
					</main>
				</AppContext.Provider>
			</AuthProvider>
    </>
  );
}

export default App;
