import { useContext } from 'react'
import { Link } from 'react-router-dom'
import { AppContext } from '../../context/context'

import SearchBar from '../SearchBar/SearchBar'

import css from './NavBar.module.css'
import ImgLogo from '../../assets/img/logo.png'


const NavBar = (props) => {

	const { 
		addRandomCharacter,
		loading
	} = useContext(AppContext)

  const agregarRandom = () => {
    const min = Math.ceil(826);
    const max = Math.floor(1);
    const id_random = Math.floor(Math.random() * (max - min + 1) + min);
    props.onSearch(id_random)
  }


  return (
		<nav className="px-6 py-2">
			<div className="w-full max-w-6xl px-6 mx-auto xl:px-0">
				<div className="grid gap-6 md:flex md:justify-between md:items-center">

					<div className="flex justify-center">
						<Link to="/">
							<img 
								src={ ImgLogo } 
								alt="Rick & Morty" 
								className="brand w-32 md:w-48" />
						</Link>
					</div>

					<div className="grid justify-end sm:flex sm:gap-x-5">
						<SearchBar 
							onSearch={props.onSearch} />

						<div className="flex gap-4 mt-4 sm:m-0">
							<button 
								className={ css.button } 
								onClick={ addRandomCharacter }
								disabled={ loading }>
									<span className={ loading ? 'hidden' : ''}>Añadir Random</span>
									{/* <span className={ loading ? '' : 'hidden' }>Cargando...</span> */}
									<span className="bg-red-500 ">Cargando...</span>
							</button>
						</div>
					</div>

				</div>
			</div>



			{/* <div className={styles.controles}>
				<button className={styles.addAll}>Añadir Todas</button>
			</div> */}
		</nav>
  );
}

export default NavBar
