import React, { useState, useEffect } from 'react'
import { Outlet, useNavigate } from 'react-router-dom'
import { AppContext } from './context/context'

import './App.css'

import NavBar from './components/NavBar/NavBar'
import Footer from './components/Footer/Footer'
import Cards from './components/Cards/Cards'

import About from './components/About/About'
import Detail from './components/Detail/Detail'
import Login from './components/Form/Login'


function App() {

	const [ characters, setCharacters ] = useState({})
	const [ loading, setLoading ] = useState(false)
  const [ access, setAccess ] = useState(false)
  const navigate = useNavigate()
  const username = process.env.USER
  const password = process.env.PASSWORD

	const getCharacter = id => {
		return fetch(`https://rickandmortyapi.com/api/character/${id}`)
			.then(resp => resp.json())
			.then(data => data)
	}

	// const generateID = () => 

	const updateCharacters = data => {
		setCharacters(data)
	}

	const deleteCharacter = id => {
		const arr = characters.filter(e => e.id !== id)
		document.getElementById(`character-${id}`).classList.add('-translate-y-[30px]', 'opacity-0')
		setTimeout(() => setCharacters(arr), 510)
	}

	const addRandomCharacter = async () => {
		setLoading(true)
		// const id = Math.floor(Math.random() * (Math.floor(1) - Math.ceil(826) + 1) + Math.ceil(826))
		id = 5
		const chars = [...characters]

		// Check for existent character
		if( chars.find( e => e.id === id ) ){
			addRandomCharacter()
			return
		}

		const char = await getCharacter(id)
		chars.unshift(char)
		setLoading(false)
		setCharacters(chars)
	}

	const handleLoading = status => setLoading(status)

	/**
  function login(userData) {
    if (userData.password === password && userData.user === username) {
      setAccess(true);
      navigate("/Home")
    }else{
      alert("Usuario o contraseña incorrecta")
    }
  }


  useEffect(() => {
    if(!access) navigate("/");
  },[access,navigate]);



	function busqueda(nombre) {
		fetch(`https://rickandmortyapi.com/api/character/${nombre}`)
			.then((response) => response.json())
			.then((data) => {
				if (data.name) {
					setCharacters((oldChars) => [...oldChars, data]);
				} else {
					window.alert("No hay personajes con ese ID");
				}
			});
  }

  const onSearch = (escrito) => {
    busqueda(escrito);
  };
	 */


  return (
    <>
			<AppContext.Provider 
				value={{
					characters,
					updateCharacters,
					deleteCharacter,
					addRandomCharacter,
					loading,
					handleLoading
				}}>
				<main id="mainWrapper">
					<NavBar path="/" />
					<Outlet/>
					<Cards />
					<Footer />
				</main>
			</AppContext.Provider>


      {/* <div className={useLocation().pathname === "/" ? "noexiste" : "existe"}>
