import { useState } from 'react'
import { validate } from './validate'
import localforage from 'localforage'
import { useNavigate } from 'react-router-dom'

import css from './Form.module.css'


const form_model = { user: '', password: '' }


export default Form => {
	const navigate = useNavigate()

	const [form, setForm] = useState({ user: 'rick@rickandmortyapp.com', password: 'RickAndMorty|2023' })
	const [errors, setErrors] = useState({})
	const [display_errors, setDisplayErrors] = useState(false)
	const allowed_user = process.env.REACT_APP_USER
  const allowed_pwd = process.env.REACT_APP_PASSWORD

	console.log(process.env)


	const handleChange = e => {
		setForm({...form, [e.target.name]: e.target.value })
		setErrors(validate({...form, [e.target.name]: e.target.value }))
	}


	const clearErrors = () => setDisplayErrors(false)


	const handleSubmit = e => {
		e.preventDefault()

		if( Object.values(errors).length === 0 ){
			console.log(allowed_user, allowed_pwd)
			const { user, password } = form

			if( 
				user === allowed_user && 
				password === allowed_pwd ){
				console.log('logeado correctamente')
			}
			// auth.login(user)
			// localforage.setItem('user', form.user)
			// 	.then(() => navigate('/'))

			// setForm({...form_model})
			// setErrors({})
		} else {
			setDisplayErrors(true)
		}
	}


	return (
		<>
			<div className="w-full max-w-xs px-6 pt-32 mx-auto xl:px-0">
				<form 
					onSubmit={ handleSubmit } 
					className="w-full grid gap-y-4">

					<div className={ css.fieldset }>
						<input
							type="text"
							className={ css.field }
							placeholder="Usuario"
							name="user"
							value={ form.user }
							onChange={ handleChange }
							onKeyUp={ clearErrors }
						/>
						{ display_errors && ( <p className={ css.errors }>{errors.user}</p> ) }

					</div>

					<div className={ css.fieldset }>
						<input
							type="password"
							value={ form.password }
							className={ css.field }
							placeholder="Contraseña"
							name="password"
							onChange={ handleChange }
							onKeyUp={ clearErrors }
						/>
						{ display_errors && ( <p className={ css.errors }>{errors.password}</p> ) }
					</div>

					<button className={ css.btn }>
						Entrar
					</button>
				</form>

				<div className="text-white pt-10 grid">
					<div>Usuario: rick@rickandmortyapp.com</div>
					<div>Contraseña: rick&Morty</div>
				</div>
			</div>
		</>
	);
}
